// Agent 응답 처리
const agentResponse = $input.first().json;
// Agent 노드는 직접 텍스트 응답을 반환
const analysis = agentResponse.text || agentResponse.message || agentResponse.content || "분석 결과를 가져올 수 없습니다.";
const inputData = $('전처리').first().json;

// 고급 신뢰도 계산 함수
function calculateAdvancedConfidence(analysis, inputData) {
  let confidence = 0.0;
  const analysisLower = analysis.toLowerCase();

  // 1. 답변 품질 평가 (40%)
  let qualityScore = 0.0;

  // 구체적인 해결책 제시 여부
  const solutionIndicators = [
    '다음과 같이', '다음 단계', '해결 방법', '조치 방법', '수정 방법',
    '1.', '2.', '3.', '단계', '절차', '명령어', '설정', '설치'
  ];
  const hasSolution = solutionIndicators.some(indicator => 
    analysisLower.includes(indicator)
  );
  if (hasSolution) qualityScore += 0.3;

  // 단계별 가이드 제공 여부
  const stepIndicators = ['먼저', '그 다음', '마지막으로', '단계별', '순서대로'];
  const hasSteps = stepIndicators.some(indicator => 
    analysisLower.includes(indicator)
  );
  if (hasSteps) qualityScore += 0.2;

  // 명확한 원인 분석 여부
  const causeIndicators = [
    '원인은', '문제는', '이유는', '발생한 이유', '오류의 원인',
    '설정 문제', '버전 충돌', '의존성', '권한', '경로'
  ];
  const hasCause = causeIndicators.some(indicator => 
    analysisLower.includes(indicator)
  );
  if (hasCause) qualityScore += 0.2;

  // 예외 상황 고려 여부
  const exceptionIndicators = [
    '만약', '경우에 따라', '주의사항', '주의해야 할', '예외적으로',
    '다른 방법', '대안', '대체'
  ];
  const hasExceptions = exceptionIndicators.some(indicator => 
    analysisLower.includes(indicator)
  );
  if (hasExceptions) qualityScore += 0.15;

  // 추가 리소스 제안 여부
  const resourceIndicators = [
    '참고', '문서', '링크', '자료', '가이드', '매뉴얼', '공식 문서'
  ];
  const hasResources = resourceIndicators.some(indicator => 
    analysisLower.includes(indicator)
  );
  if (hasResources) qualityScore += 0.15;

  confidence += qualityScore * 0.4;

  // 2. 도메인 전문성 평가 (25%)
  let domainScore = 0.0;

  // 자동차 SW 관련 키워드
  const automotiveKeywords = [
    'tasking', 'nxp', 'polyspace', 'simulink', 'autosar', 'can',
    'ecu', 'mcu', 'embedded', 'real-time', 'os', 'rtos',
    'compiler', 'linker', 'debugger', 'trace', 'profiling',
    'memory', 'flash', 'ram', 'stack', 'heap', 'interrupt'
  ];
  const automotiveMatches = automotiveKeywords.filter(keyword => 
    analysisLower.includes(keyword)
  ).length;
  domainScore += Math.min(automotiveMatches / 5, 1.0) * 0.4;

  // CI/CD 도구별 특화 키워드
  const cicdKeywords = [
    'jenkins', 'gitlab', 'github', 'pipeline', 'build', 'deploy',
    'docker', 'kubernetes', 'helm', 'yaml', 'json', 'config',
    'environment', 'variable', 'secret', 'credential'
  ];
  const cicdMatches = cicdKeywords.filter(keyword => 
    analysisLower.includes(keyword)
  ).length;
  domainScore += Math.min(cicdMatches / 3, 1.0) * 0.3;

  // 기술적 정확성 키워드
  const technicalKeywords = [
    'api', 'sdk', 'library', 'dependency', 'package', 'module',
    'interface', 'protocol', 'standard', 'specification', 'compliance'
  ];
  const technicalMatches = technicalKeywords.filter(keyword => 
    analysisLower.includes(keyword)
  ).length;
  domainScore += Math.min(technicalMatches / 3, 1.0) * 0.3;

  confidence += domainScore * 0.25;

  // 3. 답변 완성도 평가 (20%)
  let completenessScore = 0.0;

  // 답변 길이와 상세함
  if (analysis.length > 500) {
    completenessScore += 0.4;
  } else if (analysis.length > 300) {
    completenessScore += 0.3;
  } else if (analysis.length > 150) {
    completenessScore += 0.2;
  } else if (analysis.length > 50) {
    completenessScore += 0.1;
  }

  // 문단 구조 (여러 문단으로 구성)
  const paragraphCount = analysis.split('\n\n').length;
  if (paragraphCount >= 3) {
    completenessScore += 0.3;
  } else if (paragraphCount >= 2) {
    completenessScore += 0.2;
  } else if (paragraphCount >= 1) {
    completenessScore += 0.1;
  }

  // 구체적인 수치나 버전 정보
  const hasNumbers = /\d+(\.\d+)?/.test(analysis);
  if (hasNumbers) completenessScore += 0.15;

  // 코드 예제나 명령어
  const hasCode = /`[^`]+`|```|\$\s|sudo|npm|pip|git|make|cmake/.test(analysis);
  if (hasCode) completenessScore += 0.15;

  confidence += completenessScore * 0.2;

  // 4. 컨텍스트 적합성 평가 (15%)
  let contextScore = 0.0;

  // 입력된 오류 타입과의 일치도
  const errorType = inputData.error_type?.toLowerCase() || '';
  if (errorType && analysisLower.includes(errorType)) {
    contextScore += 0.3;
  }

  // 증상과 해결책의 연관성
  const symptoms = inputData.symptoms || [];
  const symptomMatches = symptoms.filter(symptom => 
    analysisLower.includes(symptom.toLowerCase())
  ).length;
  if (symptoms.length > 0) {
    contextScore += (symptomMatches / symptoms.length) * 0.4;
  }

  // 저장소 특성 반영
  const repository = inputData.repository?.toLowerCase() || '';
  if (repository && analysisLower.includes(repository)) {
    contextScore += 0.15;
  }

  // CI 로그 내용과의 연관성
  const ciLog = inputData.ci_log?.toLowerCase() || '';
  const logKeywords = ciLog.split(/\s+/).filter(word => word.length > 3);
  const logMatches = logKeywords.filter(keyword => 
    analysisLower.includes(keyword)
  ).length;
  if (logKeywords.length > 0) {
    contextScore += Math.min(logMatches / Math.min(logKeywords.length, 10), 1.0) * 0.15;
  }

  confidence += contextScore * 0.15;

  // 최종 신뢰도 정규화 (0.0 ~ 1.0)
  return Math.min(Math.max(confidence, 0.0), 1.0);
}

// 신뢰도 계산
const confidence = calculateAdvancedConfidence(analysis, inputData);

// 신뢰도 등급 결정
let confidenceLevel = '낮음';
if (confidence >= 0.8) {
  confidenceLevel = '매우 높음';
} else if (confidence >= 0.7) {
  confidenceLevel = '높음';
} else if (confidence >= 0.5) {
  confidenceLevel = '보통';
} else if (confidence >= 0.3) {
  confidenceLevel = '낮음';
} else {
  confidenceLevel = '매우 낮음';
}

return {
  json: {
    analysis: analysis,
    confidence: Math.round(confidence * 100) / 100,
    confidence_level: confidenceLevel,
    analysis_length: analysis.length,
    word_count: analysis.split(/\s+/).length
  }
};
